// Procesamiento MASIVO de 1000 links
export async function turboProcess(items) {
  const MEGA_RESULTS = [];

  for (const item of items) {
    try {
      // Extraer ASIN dinÃ¡micamente
      const url = item.json['Link Afiliado'];
      const asinMatch = url.match(/\/dp\/([A-Z0-9]{10})/);
      const asin = asinMatch ? asinMatch[1] : null;

      if (asin) {
        // Generar nuevo link con tu tag
        const TURBO_LINK = `https://www.amazon.com/dp/${asin}?tag=diegoedgard0f-20`;

        // Obtener datos de producto
        const paapiResponse = await $fetch('https://webservices.amazon.com/paapi5/getitems', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            'ItemIds': [asin],
            'PartnerTag': 'diegoedgard0f-20',
            'PartnerType': 'Associates',
            'Resources': ['Offers.Listings.Price', 'Images.Primary.Large']
          }),
          aws: {
            service: 'ProductAdvertisingAPI',
            accessKeyId: $env.AMAZON_ACCESS_KEY,
            secretAccessKey: $env.AMAZON_SECRET_KEY,
            region: 'us-east-1'
          }
        });

        const productData = paapiResponse.ItemsResult.Items[0];

        MEGA_RESULTS.push({
          ...item.json,
          'Nuevo Link': TURBO_LINK,
          'Precio Actual': productData.Offers.Listings[0].Price.DisplayAmount,
          'Imagen': productData.Images.Primary.Large.URL,
          'Status': 'ACTIVO',
          'Ãšltima VerificaciÃ³n': new Date().toISOString()
        });
      } else {
        MEGA_RESULTS.push({
          ...item.json,
          'Status': 'ERROR_ASIN',
          'Ãšltima VerificaciÃ³n': new Date().toISOString()
        });
      }
    } catch (error) {
      MEGA_RESULTS.push({
        ...item.json,
        'Status': 'ERROR_API',
        'Error': error.message,
        'Ãšltima VerificaciÃ³n': new Date().toISOString()
      });
    }
  }

  return MEGA_RESULTS;
}

return turboProcess($input.all());

