// Stats para Telegram
const stats = {
  total: $input.all().length,
  updated: $input.all().filter(item => item.json.Status === 'ACTIVO').length,
  errors: $input.all().filter(item => item.json.Status.includes('ERROR')).length,
  timestamp: new Date().toLocaleTimeString()
};

return [{
  json: stats
}];
